function sendMessage() {
    editor.sync();
    var count = editor.count("text");
    if(count>0 && service_end == false){
        var message = document.getElementById('message').value;
        if(message!= ""){
            socket.emit('message', {
                appid : config.appid,
                userid:config.userid,
                type:"message" ,
                session:config.sessionid,
                orgi:config.orgi,
                message : message
            });
        }
    }else if(service_end == true){
        alert("坐席已断开和您的对话");
    }
    editor.html('');
}
